body.codesample:
inherit flag-o-matic toolchain-funcs
src_prepare() { default
# We have a weird Makefile to work with which ignores our # compiler preferences. yay! # Note the single quotes (hence the delimiter is not an issue) sed -i -e 's:cc -O2:$(CC) $(CPPFLAGS) $(CFLAGS) $(LDFLAGS):' Makefile \ || die "sed fix failed. Uh-oh..." }
src_compile() { # -Os not happy replace-flags -Os -O2
emake CC="$(tc-getCC)" \ CPPFLAGS="${CPPFLAGS}" \ CFLAGS="${CFLAGS}" \ LDFLAGS="${LDFLAGS}" }
