EAPI=5
inherit eutils
DESCRIPTION="detox safely removes spaces and strange characters from filenames" HOMEPAGE="http://detox.sourceforge.net/" SRC_URI="mirror://sourceforge/${PN}/${P}.tar.bz2"
LICENSE="BSD" SLOT="0" KEYWORDS="~hppa ~mips ~sparc ~x86"
RDEPEND="dev-libs/popt" DEPEND="${RDEPEND} sys-devel/flex sys-devel/bison"
src_prepare() { epatch "${FILESDIR}"/${P}-destdir.patch \ "${FILESDIR}"/${P}-parallel_build.patch }
src_configure() { econf --with-popt }
src_install() { emake DESTDIR="${D}" install dodoc README CHANGES }
